<?php try { $db = new PDO('sqlite:careerguide.db'); $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION); $db->exec("PRAGMA foreign_keys = ON"); $db->exec(" CREATE TABLE IF NOT EXISTS users ( id INTEGER PRIMARY KEY AUTOINCREMENT, username TEXT UNIQUE NOT NULL, password TEXT NOT NULL ); "); $db->exec(" CREATE TABLE IF NOT EXISTS chatbot ( id INTEGER PRIMARY KEY AUTOINCREMENT, question TEXT NOT NULL, answer TEXT NOT NULL ); "); $insert = $db->prepare("INSERT INTO chatbot (question, answer) VALUES (?, ?)"); $defaultEntries = [ ["hello", "Hi there! I'm your CareerGuide assistant."], ["career", "Tell me more about your interests, and I can suggest careers."], ["engineering", "Engineering includes fields like Mechanical, Civil, Computer Science, etc."], ["bye", "Goodbye! Feel free to ask more questions anytime."] ]; foreach ($defaultEntries as $pair) { $check = $db->prepare("SELECT COUNT(*) FROM chatbot WHERE question = ?"); $check->execute([$pair[0]]); if ($check->fetchColumn() == 0) { $insert->execute($pair); } } echo "✅ Database and tables created successfully.<br>"; echo "➡️ Go to <a href='register.html'>Register</a> or <a href='index.html'>Home</a>"; } catch (PDOException $e) { echo "❌ Database error: " . $e->getMessage(); } ?>